INSERT INTO `wp_posts` (`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) VALUES (1000,1,'2009-05-15 14:48:32.000','2009-05-15 21:48:32.000','Nested and mixed lists are an interesting beast. It\'s a corner case to make sure that\n<ul>\n\t<li><span style=\"line-height:1.714285714;font-size:1rem;\">Lists within lists do not break the ordered list numbering order</span></li>\n\t<li><span style=\"line-height:1.714285714;font-size:1rem;\">Your list styles go deep enough.</span></li>\n</ul>\n<h3>Ordered - Unordered - Ordered</h3>\n<ol>\n\t<li>ordered item</li>\n\t<li>ordered item\n<ul>\n\t<li><strong>unordered</strong></li>\n\t<li><strong>unordered</strong>\n<ol>\n\t<li>ordered item</li>\n\t<li>ordered item</li>\n</ol>\n</li>\n</ul>\n</li>\n\t<li>ordered item</li>\n\t<li>ordered item</li>\n</ol>\n<h3>Ordered - Unordered - Unordered</h3>\n<ol>\n\t<li>ordered item</li>\n\t<li>ordered item\n<ul>\n\t<li><strong>unordered</strong></li>\n\t<li><strong>unordered</strong>\n<ul>\n\t<li>unordered item</li>\n\t<li>unordered item</li>\n</ul>\n</li>\n</ul>\n</li>\n\t<li>ordered item</li>\n\t<li>ordered item</li>\n</ol>\n<h3>Unordered - Ordered - Unordered</h3>\n<ul>\n\t<li>unordered item</li>\n\t<li>unordered item\n<ol>\n\t<li>ordered</li>\n\t<li>ordered\n<ul>\n\t<li>unordered item</li>\n\t<li>unordered item</li>\n</ul>\n</li>\n</ol>\n</li>\n\t<li>unordered item</li>\n\t<li>unordered item</li>\n</ul>\n<h3>Unordered - Unordered - Ordered</h3>\n<ul>\n\t<li>unordered item</li>\n\t<li>unordered item\n<ul>\n\t<li>unordered</li>\n\t<li>unordered\n<ol>\n\t<li><strong>ordered item</strong></li>\n\t<li><strong>ordered item</strong></li>\n</ol>\n</li>\n</ul>\n</li>\n\t<li>unordered item</li>\n\t<li>unordered item</li>\n</ul>','Edge Case: Nested And Mixed Lists','','publish','closed','closed','','edge-case-nested-and-mixed-lists','','','2009-05-15 14:48:32.000','2009-05-15 21:48:32.000','',0,'http://wptest.io/demo/?p=1000',0,'post','',0);
